<template>
    <div class="row g-2">
        <div class="col-lg-12">
            <label for="code" class="form-label">Code</label>
            <!-- <b-form-input id="code" v-model="form.code" :class="{'is-invalid' : form.errors.code }"
                aria-describedby="input-code-feedback" placeholder="Generated by system" disabled/> -->
            <b-form-input id="code" v-model="form.code" :class="{'is-invalid' : form.errors.code }"
                aria-describedby="input-code-feedback"/>
            <b-form-invalid-feedback id="input-code-feedback" v-html="form.errors.code"/>
        </div>
        <div class="col-lg-12">
            <label for="name" class="form-label">Name</label>
            <b-form-input id="name" v-model="form.name" :class="{'is-invalid' : form.errors.name }"
                aria-describedby="input-name-feedback" autofocus/>
            <b-form-invalid-feedback id="input-name-feedback" v-html="form.errors.name"/>
        </div>
        <div class="col-lg-12">
            <label for="site" class="form-label">Sites</label>
            <v-select
                id="site"
                v-model="form.sites"
                :options="references.sites"
                :reduce="(option) => option.id"
                :clearable="false"
                label="name"
                aria-describedby="input-site-feedback"
            />
            <b-form-invalid-feedback id="input-site-feedback" v-html="form.errors.sites"/>
        </div>
        <div class="col-lg-12">
            <label for="sbu" class="form-label">SBU</label>
            <v-select
                id="sbu"
                v-model="form.sbu_id"
                :options="references.sbus"
                :reduce="(option) => option.id"
                :clearable="false"
                label="name"
                aria-describedby="input-sbu-feedback"
            />
            <b-form-invalid-feedback id="input-sbu-feedback" v-html="form.errors.sbu_id"/>
        </div>
        <div class="col-lg-12">
            <label class="form-label">Description</label>
            <textarea class="form-control" v-model="form.description" rows="4"></textarea>
        </div>
    </div>
</template>
<script setup>
import { computed } from 'vue'
import vSelect from 'vue-select';
import 'vue-select/dist/vue-select.css';

const props = defineProps(['formData','references'])
const emit  = defineEmits(['update:formData'])

const form = computed({
    get() {
        return props.formData
    },
    set(val) {
        emit("update:formData", val)
    },
})
</script>
